class UpcomingSource {
  UpcomingSource({
      this.dates, 
      this.page, 
      this.results, 
      this.totalPages, 
      this.totalResults,});

  UpcomingSource.fromJson(dynamic json) {
    dates = json['dates'] != null ? UpcomingDates.fromJson(json['dates']) : null;
    page = json['page'];
    if (json['results'] != null) {
      results = [];
      json['results'].forEach((v) {
        results?.add(UpcomingResults.fromJson(v));
      });
    }
    totalPages = json['total_pages'];
    totalResults = json['total_results'];
  }
  UpcomingDates? dates;
  int? page;
  List<UpcomingResults>? results;
  int? totalPages;
  int? totalResults;
UpcomingSource copyWith({  UpcomingDates? dates,
  int? page,
  List<UpcomingResults>? results,
  int? totalPages,
  int? totalResults,
}) => UpcomingSource(  dates: dates ?? this.dates,
  page: page ?? this.page,
  results: results ?? this.results,
  totalPages: totalPages ?? this.totalPages,
  totalResults: totalResults ?? this.totalResults,
);
  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    if (dates != null) {
      map['dates'] = dates?.toJson();
    }
    map['page'] = page;
    if (results != null) {
      map['results'] = results?.map((v) => v.toJson()).toList();
    }
    map['total_pages'] = totalPages;
    map['total_results'] = totalResults;
    return map;
  }

}